  <script>
      export default {
        name: 'sidebar',
        methods: {
        open (link) {
            this.$electron.shell.openExternal(link)
        }
        }
    }
  </script>
  
  <style>
      ul{
          padding: 0;
          margin: 0;
          list-style: none;

      }
      .sidebar-outer{
        display: flex;
        height: 100%;
      }
      .sidebargreen{
        padding-top: 20px;  
        background: #066148;
        color: #fff;
        width: 81px;
        display: flex;
        flex-direction: column;
        align-items: center;
        position: relative;
      }
      .sidebargreen li{
          text-transform: capitalize;
          font-size: 10px;
          font-weight: 400;
      }
      .sidebargreen li.display-flex{
          display: flex;
          flex-direction: column; 
          justify-content: center;
          align-items: center;
          margin-top: 24px;
      }
      .sidebargreen li.display-flex::before{
          content: '';
          width: 0px;
          height: 56px;
          background: url(../../assets/icons/sidebar-active.svg) no-repeat left;
          display: block;
          position: absolute;
          left: 0;
          transition: 0.3s ease;
      }
      .sidebargreen li.display-flex:hover{
          font-weight: 700;
      }
      .sidebargreen li.display-flex:hover::before{
        width:4px;
